var Tests = require("test");
var Thing = require("./index");

var Tests = module.exports = Thing.Tests = Tests.extend({
	name: "ThingTests",
	run: function(){
		// this.assert_Class(Thing);
		this.assert_thing(this._thing1());
	},
	_thing1: function(){
		return new Thing({
			name: "thing1"
		});
	},
	assert_thing: function(thing){
		this.assert(thing instanceof Thing);
		this.assert(false);
	}
});

Tests();