var Mod = require("mod42/v1a");
var View = require("view42/v1");
require("./symstr.less");

var SymStrView = View.extend({
	name: "SymStrView",

	render: function(){
		var self = this;

	}
});

var SymStr = module.exports = Mod.extend({
	name: "SymStr",
	initialize: function(){
		this.render();
	},
	render: function(){
		this.view = new SymStrView({
			editor: this,
			name: this.name + "View",
			initialValue: localStorage.getItem(this.name) || this.name
		});
	},
	setValue: function(value){
		localStorage.setItem(this.name, value);
	}
});